{
    "name": "Easy Sugar Cookies",
    "description": "Quick and easy sugar cookies! They are really good, plain or with candies in them. My friend uses chocolate mints on top, and they're great!",
    "cookTime": 10,
    "prepTime": 20,
    "serving": 48,
    "isVegetarian": false,
    "category": {
        "name": "Desserts"
    },
    "user": {
        "name": "ysezgin"
    },
    "recipeInstruction": [
        {
            "stepNumber": 1,
            "description": "Preheat oven to 375 degrees F (190 degrees C). In a small bowl, stir together flour, baking soda, and baking powder. Set aside."
        },
        {
            "stepNumber": 2,
            "description": "In a large bowl, cream together the butter and sugar until smooth. Beat in egg and vanilla. Gradually blend in the dry ingredients. Roll rounded teaspoonfuls of dough into balls, and place onto ungreased cookie sheets."
        },
        {
            "stepNumber": 3,
            "description": "Bake 8 to 10 minutes in the preheated oven, or until golden. Let stand on cookie sheet two minutes before removing to cool on wire racks."
        }
    ],
    "recipeIngredient": [
        {
            "amount": 2.75,
            "unit": "cup",
            "ingredient": {
                "name": "all-purpose flour"
            }
        },
        {
            "amount": 1,
            "unit": "teaspoon",
            "ingredient": {
                "name": "baking soda"
            }
        },
        {
            "amount": 0.5,
            "unit": "teaspoon",
            "ingredient": {
                "name": "baking powder"
            }
        },
        {
            "amount": 1,
            "unit": "cup",
            "description": "softened",
            "ingredient": {
                "name": "butter"
            }
        },
        {
            "amount": 1.5,
            "unit": "cup",
            "ingredient": {
                "name": "white sugar"
            }
        },
        {
            "amount": 1,
            "ingredient": {
                "name": "egg"
            }
        },
        {
            "amount": 1,
            "unit": "teaspoon",
            "ingredient": {
                "name": "vanilla extract"
            }
        }
    ],
    "image": [
        {
            "name": "cookies",
            "url": "https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fimages.media-allrecipes.com%2Fuserphotos%2F6026039.jpg&w=596&h=596&c=sc&poi=face&q=85"
        }
    ]
}

