{
    "name": "Guacamole",
    "description": "You can make this avocado salad smooth or chunky depending on your tastes.",
    "prepTime": 10,
    "serving": 4,
    "isVegetarian": true,
    "category": {
        "name": "Appetizers and Snacks"
    },
    "user": {
        "name": "ysezgin"
    },
    "recipeInstruction": [
        {
            "stepNumber": 1,
            "description": "In a medium bowl, mash together the avocados, lime juice, and salt. Mix in onion, cilantro, tomatoes, and garlic. Stir in cayenne pepper. Refrigerate 1 hour for best flavor, or serve immediately."
        }
    ],
    "recipeIngredient": [
        {
            "amount": 3,
            "description": "peeled, pitted, and mashed",
            "ingredient": {
