            }
        },
        {
            "amount": 1,
            "unit": "teaspoon",
            "ingredient": {
                "name": "salt"
            }
        },
        {
            "amount": 0.5,
            "unit": "cup",
            "description": "diced",
            "ingredient": {
                "name": "onion"
            }
        },
        {
            "amount": 3,
            "unit": "teaspoon",
            "description": "chopped",
            "ingredient": {
                "name": "fresh cilantro"
            }
        },
        {
            "amount": 2,
            "description": "diced",
            "ingredient": {
                "name": "roma tomato"
            }
        },
        {
            "amount": 1,
            "unit": "teaspoon",
            "description": "minced",
            "ingredient": {
                "name": "garlic"
            }
        },
        {
            "amount": 1,
            "unit": "pinch",
            "description": "ground",
            "isOptional": true,
            "ingredient": {
                "name": "cayenne pepper"
            }
        }
    ],
    "image": [
        {
            "name": "guacamole",
            "url": "https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fstatic.onecms.io%2Fwp-content%2Fuploads%2Fsites%2F43%2F2015%2F08%2F4521556.jpg"
        }
    ]
}

